CREATE TABLE "drones" (
    "drone_id" INTEGER PRIMARY KEY,
    "serial_number" VARCHAR(100) UNIQUE,
    "model" VARCHAR(20),
    "weight_limit" INTEGER,
    "battery_capacity" INTEGER,
    "state" VARCHAR(15)
);
