CREATE TABLE "medications" (
    "medication_id" INTEGER PRIMARY KEY,
    "name" VARCHAR(100) UNIQUE,
    "weight" INTEGER,
    "code" VARCHAR(100) UNIQUE,
    "image_id" INTEGER,
    FOREIGN KEY ("image_id")
       REFERENCES "images" ("image_id")
);
